mapper.select:
SELECT ROWNUM rn, boardData.*, upData.* FROM (select boardMenu.cafe_id, boardMenu.board_menu_name, boardMenu.board_menu_type, board.*, cafeMember.cafe_user_nickname, cafeMember.cafe_user_image_num, cafeMember.cafe_user_grade, cafeMember.cafe_user_visit, cafeMember.cafe_user_reply, cafeMember.cafe_user_visit_check, cafeMember.cafe_user_regdate, cafeMember.cafe_user_applydate, cafeMember.cafe_user_status FROM (SELECT cafe_id, board_menu_num, board_menu_name, board_menu_type FROM project_board_type WHERE cafe_id = #{cafeId}
AND board_menu_num = #{boardMenuNum}
) boardMenu, (SELECT * FROM project_board WHERE board_title LIKE '%' || '' || '%') board, (SELECT * FROM project_cafe_member) cafeMember WHERE boardMenu.board_menu_num = board.board_menu_num AND boardMenu.cafe_id = cafeMember.cafe_id) boardData LEFT JOIN (SELECT cafe_id, cafe_user_grade, cut_name from project_member_grade) grade ON boardData.cafe_id = grade.cafe_id and boardData.cafe_user_grade = grade.cafe_user_grade LEFT JOIN (SELECT cafe_user_grade up_cafe_user_grade, cut_name up_cut_name FROM project_member_grade) upData ON boardData.cafe_user_grade+1 = upData.up_cafe_user_grade ORDER BY boardData.board_num DESC

select J.* from(select rownum rn, I.* from
(select B.*,C.cafe_user_nickname from project_board B inner join project_cafe_member C
on B.user_id = C.user_id where B.board_menu_num=#{boardMenuNum} and C.cafe_id=#{cafeId}
order by board_num desc)I)J 
where rn between #{start} and #{end}
